import { useState } from "react";

const SearchBar = ({ onSearch }) => {
    const [city, setCity] = useState(''); // State to hold the input value

    // Handle input changes
    const handleChange = (event) => {
        setCity(event.target.value); // Update city state on input change
    };

    // Handle key press
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            event.preventDefault(); // Prevent default form submission
            if (city.trim() !== '') {
                onSearch(city); // Trigger search with the current city
            }
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Enter Zip Code / State / City"
                value={city}
                onChange={handleChange}
                onKeyDown={handleKeyDown}
            />
        </div>
    );
};

export default SearchBar;
