const WeatherDisplay = ({data}) => {
    console.log(data.temp)
    const back = (conditions) => {
        if(conditions.includes('cloudy'))
            document.body.style.background = 'linear-gradient(to bottom, #d3d3d3, #808080)'; 

    }
    return (
        <div>
            <h3>Conditions: {data.conditions}</h3> {back(data.conditions)}
            <h3>Temperature: {data.temp}</h3>
            <h3>Feels Like: {data.feelslike}</h3>
            <h3>Humidity: {data.humidity}%</h3>
        </div>
    )
}
export default WeatherDisplay;
